INSERT INTO book (name, description, author) VALUES ('O Senhor dos Anéis', 'Uma épica história de fantasia que segue a jornada de Frodo Bolseiro, que deve destruir um anel poderoso para impedir que ele caia nas mãos do Senhor das Trevas, Sauron.', 'J.R.R. Tolkien');
INSERT INTO book (name, description, author) VALUES ('1984', 'Um romance distópico que explora um futuro totalitário onde o governo controla até os pensamentos dos cidadãos, e a liberdade individual é esmagada.', 'George Orwell');
INSERT INTO book (name, description, author) VALUES ('Dom Quixote', 'A história de um nobre que, influenciado pela leitura de romances de cavalaria, acredita ser um cavaleiro errante e sai em busca de aventuras.', 'Miguel de Cervantes');
INSERT INTO book (name, description, author) VALUES ('O Grande Gatsby', 'Uma história sobre o sonho americano e a decadência da sociedade da década de 1920, focada na vida de Jay Gatsby e sua obsessão por Daisy Buchanan.', 'F. Scott Fitzgerald');
INSERT INTO book (name, description, author) VALUES ('Matar a Saudade', 'Um romance sobre o impacto da saudade na vida de uma mulher, lidando com questões de perda e reencontro.', 'Clarice Lispector');
INSERT INTO book (name, description, author) VALUES ('O Código Da Vinci', 'Um thriller de mistério que segue Robert Langdon enquanto ele tenta resolver enigmas em torno de um assassinato no Museu do Louvre.', 'Dan Brown');
INSERT INTO book (name, description, author) VALUES ('Orgulho e Preconceito', 'A história de Elizabeth Bennet, uma jovem mulher que lida com questões de classe social, casamento e orgulho em sua relação com o orgulhoso Sr. Darcy.', 'Jane Austen');
INSERT INTO book (name, description, author) VALUES ('O Pequeno Príncipe', 'Uma história filosófica e encantadora sobre um pequeno príncipe que viaja por planetas, aprendendo lições sobre amor, amizade e a natureza humana.', 'Antoine de Saint-Exupéry');
INSERT INTO book (name, description, author) VALUES ('Cem Anos de Solidão', 'A saga da família Buendía, que se desenrola ao longo de várias gerações, em uma história repleta de realismo mágico e simbolismo.', 'Gabriel García Márquez');
INSERT INTO book (name, description, author) VALUES ('Frankenstein', 'O romance gótico que conta a história de Victor Frankenstein e sua criação, um monstro que busca aceitação e vingança.', 'Mary Shelley');